#include "parsestack.h"

bool errorRecovery(Parser &p, std::vector<stackitem> &stack, Token &lookahead, Error &e)
{
    if (singleTok(p, stack, lookahead, e))
        return true;
    else
        return panicMode(p, stack, lookahead, e);
}

