Token Parser::consume()
{
    Token cur;
    while (true)
    {
        cur = lexer.nextToken();
        if (cur.type != TokenType::ERROR) return cur;

        Error(Error::MsgType::ERROR, cur, cur.message)
            .primary(Error::Primary(cur)
                .error(cur.message))
            .report();
    }

    return cur;
}

void Parser::invalidSyntax(Token t)
{
    Error(Error::MsgType::ERROR, t, "Invalid syntax")
        .primary(Error::Primary(t)
                .error("Invalid syntax"))
        .report();
}
